update basemst.bbd_payment_order po
left join basemst.bbd_loan_order loan on loan.id = po.loan_order_id
set po.payer = loan.borrower
where po.loan_order_id in  ('RZ20170527003','RZ20170526004','RZ20170526003','RZ20170526002','RZ20170525001','RZ20170523004','RZ20170523003','RZ20170523002','RZ20170523001','RZ20170522007','RZ20170522004','RZ20170522003','RZ20170522002','RZ20170519023','RZ20170519021','RZ20170519017','RZ20170519015','RZ20170519008','RZ20170519005','RZ20170519002','RZ20170519001','RZ20170518001','RZ20170517005','RZ20170517004','RZ20170517001','RZ20170516008','RZ20170516006','RZ20170516004','RZ20170516003','RZ20170516001','RZ20170515010','RZ20170515007','RZ20170515004','RZ20170515003','RZ20170515002','RZ20170510001','RZ20170508001','RZ20170504002','RZ20170502001','RZ20170427002','RZ20170421002','RZ20170417008','RZ20170417003','RZ20170417002','RZ20170415001','RZ20170405008','RZ20170328002','RZ20170317008','RZ20170317006','RZ20170317004','RZ20170317002','RZ20170313001','RZ20170309005','RZ20170309004','RZ20161208007','RZ20161201001','RZ20161128001','RZ20161017001','RZ20160810028','RZ20160726003')
and po.payment_type='应抵价差'
and po.memo='SYSTEM';
select concat(substring(po.id,1,10),'2',substring(po.id,12)),po.create_time,loan2.id, po.payer,po.payment_type,po.amount,po.total_amount,po.status,loan2.provider,po.memo from basemst.bbd_payment_order po
left join `bbd_loan_order` loan on loan.id = po.`loan_order_id` 
left join basemst.bbd_loan_order loan2 on loan2.order_group_id = loan.`order_group_id`  and loan2.`id` !=loan.`id` 
where po.loan_order_id in  ('RZ20170527003','RZ20170526004','RZ20170526003','RZ20170526002','RZ20170525001','RZ20170523004','RZ20170523003','RZ20170523002','RZ20170523001','RZ20170522007','RZ20170522004','RZ20170522003','RZ20170522002','RZ20170519023','RZ20170519021','RZ20170519017','RZ20170519015','RZ20170519008','RZ20170519005','RZ20170519002','RZ20170519001','RZ20170518001','RZ20170517005','RZ20170517004','RZ20170517001','RZ20170516008','RZ20170516006','RZ20170516004','RZ20170516003','RZ20170516001','RZ20170515010','RZ20170515007','RZ20170515004','RZ20170515003','RZ20170515002','RZ20170510001','RZ20170508001','RZ20170504002','RZ20170502001','RZ20170427002','RZ20170421002','RZ20170417008','RZ20170417003','RZ20170417002','RZ20170415001','RZ20170405008','RZ20170328002','RZ20170317008','RZ20170317006','RZ20170317004','RZ20170317002','RZ20170313001','RZ20170309005','RZ20170309004','RZ20161208007','RZ20161201001','RZ20161128001','RZ20161017001','RZ20160810028','RZ20160726003')
and po.payment_type='应抵价差'
and po.memo='SYSTEM';
update bbd_loan_order loan
JOIN `bbd_payment_order` po on po.`loan_order_id` =loan.id
set loan.`difference_In_Price` =po.`total_amount` 
where po.`payment_type` ='应抵价差';
